import { Component } from '@angular/core';
import { Inversor } from '../modelo/Inversor';
import { InversorService } from '../servico/inversor.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
  //Objeto do tipo Inversor
  inversor = new Inversor();


  //Variavel para visibilidade dos botoes
  btnCadastro:boolean = true;

  //Json de inversores
  inversores:Inversor[] = [];

  //Construtor
  constructor(private servico:InversorService){}

  //Método de seleção
  selecionar():void{
    this.servico.selecionar()
    .subscribe(retorno =>this.inversores = retorno);

  }
   //Método de cadastro
   cadastrar():void{
    this.servico.cadastrar(this.inversor).subscribe(retorno =>{this.inversores.push(retorno);});

  }

  //Método de inicialização
  ngOnInit(){
    this.selecionar();
  }

}
<form>

    <img src="assets/Inversor.png">
    <input type="numb" placeholder="Código Inversor" class="form-control"   name="Código Inversor" [(ngModel)]="inversor.codigo_inversor">
    <input type="numb" placeholder="NS Inversor" class="form-control"   name="NS Inversor" [(ngModel)]="inversor.ns_inversor">
    <input type="numb" placeholder="NS Datalogguer" class="form-control"   name="NS Datalogguer" [(ngModel)]="inversor.ns_datalogger">
    <input type="numb" placeholder="Nota fiscal" class="form-control"   name="Nota fiscal" [(ngModel)]="inversor.nf">
    <input type="numb" placeholder="CNPJ" class="form-control"   name="CNPJ" [(ngModel)]="inversor.cnpj">
    <input type="text" placeholder="Marca Inversor" class="form-control"   name="Marca" [(ngModel)]="inversor.marca">
    <input type="numb" placeholder="Data" class="form-control"   name="Data" [(ngModel)]="inversor.data">
    <input type="text" placeholder="Observações" class="form-control"   name="" [(ngModel)]="inversor.observacoes">

    <input type="button" value="Cadastrar" *ngIf="btnCadastro" class="btn btn-primary" (click ="cadastrar()")>
    <input type="button" value="Alterar" *ngIf="!btnCadastro"  class="btn btn-warning">
    <input type="button" value="Remover" *ngIf="!btnCadastro"  class="btn btn-danger">
    <input type="button" value="Cancelar"*ngIf="!btnCadastro"  class="btn btn-secondary">


</form>



<table class="table">
    <thead>
        <tr>
            <th>#</th>
            <th>Código Inversor</th>
            <th>NS Inversor</th>
            <th>NS Datalogguer</th>
            <th>Nota fiscal</th>
            <th>CNPJ</th>
            <th>Marca Inversor</th>
            <th>Data</th>
            <th>Observações</th>

        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let i of inversores;index as id">
            <td>{{id+1}}</td>
            <td>{{i.ns_inversor}}</td>
            <td>{{i.ns_inversor}}</td>
            <td>{{i.ns_datalogger}}</td>
            <td>{{i.nf}}</td>
            <td>{{i.cnpj}}</td>
            <td>{{i.marca}}</td>
            <td>{{i.data}}</td>
            <td>{{i.observacoes}}</td>
            <td><button class="btn btn-success">Selecionar</button> </td>


        </tr>
    </tbody>
</table>
