import { Component } from '@angular/core';
import { Inversor } from '../modelo/Inversor';
import { InversorService } from '../servico/inversor.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
  //Objeto do tipo Inversor
  inversor = new Inversor();


  //Variavel para visibilidade dos botoes
  btnCadastro:boolean = true;

  //Variavel para visibilidade da tabela
  tabela:boolean = true;

  //Json de inversores
  inversores:Inversor[] = [];

  //Construtor
  constructor(private servico:InversorService){}

  //Método de seleção
  selecionar():void{
    this.servico.selecionar()
    .subscribe(retorno =>this.inversores = retorno);

  }
   //Método de cadastro 
   cadastrar():void{
    this.servico.cadastrar(this.inversor).subscribe(retorno =>{
      //Cadastro de inversor no vetor
      this.inversores.push(retorno);

    //Limpar formulario
    this.inversor = new Inversor();

    //Mensagem
    alert('Inversor cadastrado com sucesso!')

  });
}
  //Metodo para selecionar inversor
  selecionarInversor(posicao:number):void{

    //Seleciona inversor no vetor]
    this.inversor = this.inversores[posicao];
    //Visibilidade dos botoes
    this.btnCadastro = false;

    //Visibilidade da tabela
    this.tabela = false;
  }

  //Método para editar inversor
  editar(): void{
    this.servico.editar(this.inversor)
    .subscribe(retorno =>{
      //Obter posição do vetor onde está o inversor
      let posicao = this.inversores.findIndex(obj => {
        return obj.codigo == retorno.codigo;
      });

      //Alterar os dados do cliente no vetor
      this.inversores[posicao] = retorno;

       //Limpar formulario
    this.inversor = new Inversor();

       //Visibilidade dos botoes
    this.btnCadastro = true;

    //Visibilidade da tabela
    this.tabela = true;
     //Mensagem
     alert('Inversor alterado com sucesso!')
    });
  }

  //Método de inicialização
  ngOnInit(){
    this.selecionar();
  }

}
<form>

    <img src="assets/Inversor.png">
    <input type="numb" placeholder="Código Inversor" class="form-control"   name="Código Inversor" [(ngModel)]="inversor.codigo_inversor">
    <input type="numb" placeholder="NS Inversor" class="form-control"   name="NS Inversor" [(ngModel)]="inversor.ns_inversor">
    <input type="numb" placeholder="NS Datalogguer" class="form-control"   name="NS Datalogguer" [(ngModel)]="inversor.ns_datalogger">
    <input type="numb" placeholder="Nota fiscal" class="form-control"   name="Nota fiscal" [(ngModel)]="inversor.nf">
    <input type="numb" placeholder="CNPJ" class="form-control"   name="CNPJ" [(ngModel)]="inversor.cnpj">
    <input type="text" placeholder="Marca Inversor" class="form-control"   name="Marca" [(ngModel)]="inversor.marca">
    <input type="numb" placeholder="Data" class="form-control"   name="Data" [(ngModel)]="inversor.data">
    <input type="text" placeholder="Observações" class="form-control"   name="Observações" [(ngModel)]="inversor.observacoes">

    <input type="button" value="Cadastrar" *ngIf="btnCadastro" class="btn btn-primary" (click) ="cadastrar()">
    <input type="button" value="Alterar" *ngIf="!btnCadastro"  class="btn btn-warning" (click) ="editar()">
    <input type="button" value="Remover" *ngIf="!btnCadastro"  class="btn btn-danger">
    <input type="button" value="Cancelar"*ngIf="!btnCadastro"  class="btn btn-secondary">


</form>



<table class="table" *ngIf="tabela">
    <thead>
        <tr>
            <th>#</th>
            <th>Código Inversor</th>
            <th>NS Inversor</th>
            <th>NS Datalogguer</th>
            <th>Nota fiscal</th>
            <th>CNPJ</th>
            <th>Marca Inversor</th>
            <th>Data</th>
            <th>Observações</th>

        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let i of inversores;index as id">
            <td>{{id+1}}</td>
            <td>{{i.ns_inversor}}</td>
            <td>{{i.ns_inversor}}</td>
            <td>{{i.ns_datalogger}}</td>
            <td>{{i.nf}}</td>
            <td>{{i.cnpj}}</td>
            <td>{{i.marca}}</td>
            <td>{{i.data}}</td>
            <td>{{i.observacoes}}</td>
            <td><button class="btn btn-success"(click)="selecionarInversor(id)"> Selecionar</button> </td>


        </tr>
    </tbody>
</table>
