import { Component } from '@angular/core';
import { Inversor } from '../modelo/Inversor';
import { InversorService } from '../servico/inversor.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {

  //Variavel para visibilidade dos botoes

  btnCadastro:boolean = true;

  //Json de inversores
  inversores:Inversor[] = [];

  //Construtor
  constructor(private servico:InversorService){}

  //Método de seleção
  selecionar():void{
    this.servico.selecionar()
    .subscribe(retorno =>this.inversores = retorno);

  }

  //Método de inicialização
  ngOnInit(){
    this.selecionar();
  }

}
<form>

    <img src="assets/Inversor.png">
    <input type="numb" placeholder="Código Inversor" class="form-control">
    <input type="numb" placeholder="NS Inversor" class="form-control">
    <input type="numb" placeholder="NS Datalogguer" class="form-control">
    <input type="numb" placeholder="Nota fiscal" class="form-control">
    <input type="numb" placeholder="CNPJ" class="form-control">
    <input type="text" placeholder="Marca Inversor" class="form-control">
    <input type="numb" placeholder="Data" class="form-control">
    <input type="text" placeholder="Observações" class="form-control">

    <input type="button" value="Cadastrar" *ngIf="btnCadastro" class="btn btn-primary">
    <input type="button" value="Alterar" *ngIf="!btnCadastro"  class="btn btn-warning">
    <input type="button" value="Remover" *ngIf="!btnCadastro"  class="btn btn-danger">
    <input type="button" value="Cancelar"*ngIf="!btnCadastro"  class="btn btn-secondary">


</form>

<table class="table">
    <thead>
        <tr>
            <th>#</th>
            <th>Código Inversor</th>
            <th>NS Inversor</th>
            <th>NS Datalogguer</th>
            <th>Nota fiscal</th>
            <th>CNPJ</th>
            <th>Marca Inversor</th>
            <th>Data</th>
            <th>Observações</th>

        </tr>
    </thead>
    <tbody>
        <tr>*ngFor="let i of inversores"
            <td></td>
            <td>{{i.ns_inversor}}</td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
        </tr>
    </tbody>
</table>
